def sconv(time):
    mm, ss = time.split(':')
    return int(mm)*60 + int(ss)
def column(matrix, c,v):
    column = [row[c] for row in matrix]
    score = [row[0] for row in matrix]
    if v > column[0]:
        return 100
    elif v < column[-1]:
        return 0
    while v not in column:
        v = (round(v,2)-(1/10)) 
    return score[column.index(v)]  
def timecolumn(matrix, c,v):
    column = [row[c] for row in matrix]
    score = [row[0] for row in matrix]
    while v not in column:
        if v < column[0]:
            return 100
        elif v > column[-1]:
            return 0
        v = (round(v,2)+ (1))    
    return score[column.index(v)] 
